%% To calculate the distances between each nodes
map.n = length(x); 

for i = 1 : map.n
    map.node(i).x = x(i);
    map.node(i).y = y(i);
end

[~, SortOrder]=sort([map.node.x]);
map.node=map.node(SortOrder);

map.edges = zeros(map.n,map.n);

for i = 1 : map.n
    for j = 1: map.n
        x1 = map.node(i).x ;
        x2 = map.node(j).x;
        y1 = map.node(i).y;
        y2 = map.node(j).y;
        
        map.edges(i,j) = sqrt((x1 - x2) ^2 + (y1 - y2)^2); 
        
    end
end

map.model.x = x(SortOrder);
map.model.y = y(SortOrder);
end